                <?php
                $host = 'localhost';
                $db = 'grupocardinalespage';
                $user = 'root';
                $pass = 'root';
                $charset = 'utf8mb4';

                $dsn = "mysql:host=$host;dbname=$db;charset=$charset";
                $opt = [
                    PDO::ATTR_ERRMODE => PDO::ERRMODE_EXCEPTION,
                    PDO::ATTR_DEFAULT_FETCH_MODE => PDO::FETCH_ASSOC,
                    PDO::ATTR_EMULATE_PREPARES => false,
                ];
                $pdo = new PDO($dsn, $user, $pass, $opt);

                $stmt = $pdo->prepare('SELECT * FROM clientes');
                $stmt->execute();

                $clientes = $stmt->fetchAll();

                foreach ($clientes as $cliente) {
                    echo '
                        <div class="cliente-div">
                            <button
                            class="w-full text-gray-500 hover:text-blue-700 rounded-lg hover:border-blue-700 focus:ring-4 focus:outline-none focus:ring-blue-300 focus:border-0 focus:text-blue-700 text-center inline-flex items-center me-2"
                            type="button" ondblclick="window.location.href=\'cliente?noCliente=' . $cliente['noCliente'] . '\'">
