    <script>
        document.getElementById('buscador').addEventListener('keyup', function () {
            var input = this.value.toUpperCase();
            var clientes = document.getElementsByClassName('cliente-div');

            for (var i = 0; i < clientes.length; i++) {
                var numero = clientes[i].getElementsByClassName('cliente-numero')[0].innerText.toUpperCase();
                if (numero.indexOf(input) > -1) {
                    clientes[i].style.display = '';
                } else {
                    clientes[i].style.display = 'none';
                }
            }
        });
    </script>
