function traducirFecha($fhticket)
{
    $dia = date('d', strtotime($fhticket));
    $mes = date('m', strtotime($fhticket));
    $hora = date('h:i', strtotime($fhticket));
    $am_pm = strtoupper(date('a', strtotime($fhticket)));
    $meses = array(
        '01' => 'Ene',
        '02' => 'Feb',
        '03' => 'Mar',
        '04' => 'Abr',
        '05' => 'May',
        '06' => 'Jun',
        '07' => 'Jul',
        '08' => 'Ago',
        '09' => 'Sep',
        '10' => 'Oct',
        '11' => 'Nov',
        '12' => 'Dic'
    );
    return $dia . ' de ' . $meses[$mes] . ' a las ' . $hora . ' ' . $am_pm . ' del ' . date('Y', strtotime($fhticket));
}

function getStatus($status)
{
    switch ($status) {
        case "1":
            echo 'Creado';
            break;
        case "2":
            echo 'Asignado';
            break;
        case "3":
            echo 'Arribo';
            break;
        case "4":
            echo 'Inicio';
            break;
        case "5":
            echo 'Realizaci贸n';
            break;
        case "6":
            echo 'Finalizaci贸n';
            break;
        case "7":
            echo 'Programado';
            break;
        case "8":
